CustomCommands.CustomCommand:
param( [string]$BaseUrl, [string]$User_id, [string]$client_id, [string]$token_url, [string]$private_key )
$uri = "$BaseUrl/oauth/idp"
$assertionHeader = @{ "Content-Type" = "application/x-www-form-urlencoded" }
$body = @{ user_id = $User_id client_id = $client_id token_url = $token_url private_key = $private_key
}
return Invoke-RestMethod -Method Post -Uri $uri -Headers $assertionHeader -Body $body

param( [string]$BaseUrl, [String]$client_id, [String]$company_id, [String]$grant_type, [String]$assertion # Planned support for new_token #[bool]$new_token )
$uri = "$BaseUrl/oauth/token"
$tokenHeader = @{ "Content-Type" = "application/x-www-form-urlencoded" }
$body = @{ client_id = $client_id company_id = $company_id grant_type = $grant_type assertion = $assertion #new_token = $new_token }
return Invoke-RestMethod -Method Post -Uri $uri -Headers $tokenHeader -Body $body

Param( [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$BaseUrl, [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$AuthUrl, [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$TokenUrl, [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$ClientId, [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$ClientSecret, [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$UserId, [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$CompanyId, [parameter(Mandatory=$true,ValueFromPipelineByPropertyName=$true)] [ValidateNotNullOrEmpty()] [string]$GrantType )
try { $assertion = Get-SFAssertion -BaseUrl $AuthUrl -client_id $ClientId -token_url $TokenUrl -private_key $ClientSecret -User_id $UserId $token = Get-SFToken -BaseUrl $AuthUrl -client_id $ClientId -grant_type $GrantType -company_id $CompanyId -assertion $assertion
$global:_token = $token $global:_url = $BaseUrl } catch { throw }


                    param(
                        [string]$BaseUrl,
                        [string]$EndPoint,
                        [string]$Append
                    )

                    $headers        = Get-SFHeaders
					$jsonHeaders	= $headers | ConvertTo-Json
                    $uri            = $BaseUrl + '/' + $EndPoint + $Append
					try
				    {


    					$result         = Invoke-RestMethod -Method Get -Uri $uri -Headers $headers
    					$rows = $result.d.results
					    While($result.d.__Next){
        					$result     = Invoke-RestMethod -Method Get -Uri $result.d.__Next -Headers $headers
        					$rows       += $result.d.Results
    					}
    					return $rows

                    	#$result = Invoke-RestMethod -Method Get -Uri $uri -Headers $headers
                    	#return $result
					}
					catch {
						Throw #"$($uri) - $($jsonHeaders)"
					}


                    param(
                        [string]$BaseUrl,
                        [string]$EndPoint,
                        [string]$Append
                    )

                    $headers        = Get-SFHeaders
					$jsonHeaders	= $headers | ConvertTo-Json
                    $uri            = $BaseUrl + '/' + $EndPoint + $Append
					try
				    {
    					$result         = Invoke-RestMethod -Method Get -Uri $uri -Headers $headers    					
                    	return $result.d
					}
					catch {
						Throw #"$($uri) - $($jsonHeaders)"
					}



                    param()
                    $today = Get-Date
					[string]$fromDate = $today.AddDays(1).ToString("yyyy-MM-dd")
					[string]$startDate = $today.ToString("yyyy-MM-dd")
					[string]$toDate   = $today.AddDays(91).ToString("yyyy-MM-dd")
                    $Append = '?fromDate=' + $fromDate + '&toDate=' + $toDate + '&$filter=startDate gt datetime''' + $startDate + 'T23:59:59Z'''
                    $result = Invoke-SFRestMethod -BaseUrl $global:_url -EndPoint "odata/v2/EmpEmployment" -Append $Append
					# throw "$($result.count)"
					$futureusers = @()
					foreach( $empl in $result) { 
						try {
							$userId = "('$($empl.UserId)')"
							$AppendUser = $userId + '?$expand=manager,empInfo,empInfo/jobInfoNav'
							$userResult = Invoke-SFRestMethodSingle -BaseUrl $global:_url -EndPoint "odata/v2/User" -Append $AppendUser
							$futureusers += $userResult
						}
						catch {
							if($_.Exception.StatusCode -eq "NotFound") {
								# Skip
							}
							else {
								Throw
							}
						}
					} 
					$global:_sfFutureUsers = $futureusers
					return $futureusers
